mapper.select:
select 

from MemberCard
where memID = #{memid,jdbcType=DECIMAL}
  and mcID = #{mcid,jdbcType=DECIMAL}

mapper.delete:
delete from MemberCard
where memID = #{memid,jdbcType=DECIMAL}
  and mcID = #{mcid,jdbcType=DECIMAL}

mapper.update:
update MemberCard
set merID = #{merid,jdbcType=DECIMAL},
  mcType = #{mctype,jdbcType=VARCHAR},
  mcCredit = #{mccredit,jdbcType=REAL},
  mcBalance = #{mcbalance,jdbcType=REAL},
  mcEnable = #{mcenable,jdbcType=BIT}
where memID = #{memid,jdbcType=DECIMAL}
  and mcID = #{mcid,jdbcType=DECIMAL}
